import React from 'react';
import { Link } from 'react-router-dom';
import '../styles/Home.css';

const Home = () => {
  // دسته‌بندی امکانات
  const featureCategories = [
    {
      title: "توابع هش و امنیت",
      items: [
        { name: "ساخت هش", path: "/hash-generator", description: "تولید هش با الگوریتم‌های MD5، SHA-1، SHA-256 و موارد دیگر" },
        { name: "هش بصری", path: "/visual-hash", description: "مشاهده نمایش‌های بصری مقادیر هش" },
        { name: "شکستن رمز عبور", path: "/password-cracker", description: "شبیه‌سازی تکنیک‌های شکستن رمز عبور" },
        { name: "نمایش برخورد هش", path: "/hash-collision", description: "نمایش و بررسی برخورد در توابع هش" },
      ]
    },
    {
      title: "ابزارهای کاربردی",
      items: [
        { name: "پیدا کردن فایل‌های تکراری", path: "/file-deduplicator", description: "شناسایی فایل‌های تکراری با استفاده از هش" },
        { name: "مدیریت رمزعبور", path: "/password-manager", description: "ابزار ذخیره‌سازی ایمن رمزهای عبور" },
        { name: "تولید آواتار بر اساس هش", path: "/identicon-generator", description: "تولید آواتار شخصی بر اساس هش" },
        { name: "پیدا کردن تصاویر مشابه", path: "/similar-image-finder", description: "استفاده از هش برای پیدا کردن تصاویر مشابه" },
      ]
    },
    {
      title: "آموزش و یادگیری",
      items: [
        { name: "آموزش رمزنگاری", path: "/learn-cryptography", description: "یادگیری مفاهیم پایه رمزنگاری و هش" },
        { name: "انیمیشن SHA-256", path: "/sha256-animation", description: "نمایش مصور عملکرد الگوریتم SHA-256" },
        { name: "هش در بیوانفورماتیک", path: "/bioinformatics", description: "کاربردهای هش در علوم زیستی و بیوانفورماتیک" },
      ]
    },
    {
      title: "کاربردهای پیشرفته",
      items: [
        { name: "تولید NFT بر اساس هش", path: "/nft-generator", description: "ایجاد توکن‌های غیرقابل تعویض با هش" },
        { name: "پیام‌رسانی همتا به همتا", path: "/p2p-messaging", description: "ارسال پیام رمزنگاری شده بین کاربران" },
      ]
    },
  ];

  return (
    <div className="home">
      <div className="hero">
        <h1>به مرکز جامع هش و رمزنگاری خوش آمدید</h1>
        <p>توابع هش، رمزنگاری و کاربردهای متنوع آنها را کشف کنید</p>
      </div>

      <section className="feature-categories">
        {featureCategories.map((category, catIndex) => (
          <div className="feature-category" key={catIndex}>
            <h2>{category.title}</h2>
            <div className="features">
              {category.items.map((item, itemIndex) => (
                <div className="feature-card" key={itemIndex}>
                  <h3>{item.name}</h3>
                  <p>{item.description}</p>
                  <Link to={item.path} className="btn">مشاهده</Link>
                </div>
              ))}
            </div>
          </div>
        ))}
      </section>

      <div className="info-section">
        <h2>توابع هش چیستند؟</h2>
        <p>
          توابع هش الگوریتم‌های ریاضی هستند که داده‌ها با اندازه دلخواه را به مقادیر با اندازه ثابت تبدیل می‌کنند.
          آن‌ها برای یکپارچگی داده، ذخیره رمز عبور و امضاهای دیجیتال ضروری هستند.
        </p>
        
        <h3>الگوریتم‌های هش متداول:</h3>
        <ul>
          <li><strong>MD5</strong> - سریع اما آسیب‌پذیر در برابر تصادم</li>
          <li><strong>SHA-1</strong> - الگوریتم قدیمی‌تر با نقاط ضعف شناخته شده</li>
          <li><strong>SHA-256</strong> - بخشی از خانواده SHA-2، امروزه به طور گسترده استفاده می‌شود</li>
          <li><strong>SHA-512</strong> - امنیت بالاتر با خروجی ۵۱۲ بیتی</li>
        </ul>
      </div>
    </div>
  );
};

export default Home;
