import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import "../styles/PasswordCracker.css";
import { API_BASE_URL } from "../config";

const PasswordCracker = () => {
  const [hashValue, setHashValue] = useState("");
  const [algorithm, setAlgorithm] = useState("md5");
  const [method, setMethod] = useState("dictionary");
  const [crackResult, setCrackResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [crackingProgress, setCrackingProgress] = useState(0);
  const progressInterval = useRef(null);
  const [error, setError] = useState(null);
  const [passwordToHash, setPasswordToHash] = useState("");
  const [generatedHash, setGeneratedHash] = useState("");
  const [timeEstimates, setTimeEstimates] = useState(null);
  const [samplePasswords, setSamplePasswords] = useState({});
  const [loadingSamples, setLoadingSamples] = useState({
    low: true,
    medium: true,
    high: true,
    "very-high": true,
    persian: true,
  });
  const [statusMessage, setStatusMessage] = useState("");

  const algorithms = ["md5", "sha1", "sha256", "sha512"];
  const methods = [
    { value: "dictionary", label: "Ø­Ù…Ù„Ù‡ ÙØ±Ù‡Ù†Ú¯ Ù„ØºØª (Dictionary Attack)" },
    { value: "bruteforce", label: "Ø­Ù…Ù„Ù‡ Ø¬Ø§Ù…Ø¹ (Brute Force)" },
    { value: "pattern", label: "Ø­Ù…Ù„Ù‡ Ø§Ù„Ú¯Ùˆ (Pattern-based)" },
    { value: "hybrid", label: "Ø­Ù…Ù„Ù‡ ØªØ±Ú©ÛŒØ¨ÛŒ (Hybrid)" },
    { value: "rainbow", label: "Ø¬Ø¯Ø§ÙˆÙ„ Ø±Ù†Ú¯ÛŒÙ†â€ŒÚ©Ù…Ø§Ù† (Rainbow Tables)" },
  ];

  const complexities = [
    { value: "low", label: "Ø³Ø§Ø¯Ù‡ (ÙÙ‚Ø· Ø§Ø¹Ø¯Ø§Ø¯)", example: "1234" },
    { value: "medium", label: "Ù…ØªÙˆØ³Ø· (Ø­Ø±ÙˆÙ Ú©ÙˆÚ†Ú© Ùˆ Ø§Ø¹Ø¯Ø§Ø¯)", example: "abc123" },
    {
      value: "high",
      label: "Ù¾ÛŒÚ†ÛŒØ¯Ù‡ (Ø­Ø±ÙˆÙ Ø¨Ø²Ø±Ú¯ØŒ Ú©ÙˆÚ†Ú© Ùˆ Ø§Ø¹Ø¯Ø§Ø¯)",
      example: "Abc123Def",
    },
    {
      value: "very-high",
      label: "Ø¨Ø³ÛŒØ§Ø± Ù¾ÛŒÚ†ÛŒØ¯Ù‡ (Ù‡Ù…Ù‡ Ú©Ø§Ø±Ø§Ú©ØªØ±Ù‡Ø§)",
      example: "P@s$w0rd!123",
    },
    {
      value: "persian",
      label: "ÙØ§Ø±Ø³ÛŒ (Ø­Ø±ÙˆÙ ÙØ§Ø±Ø³ÛŒ Ùˆ Ø§Ø¹Ø¯Ø§Ø¯)",
      example: "Ø§ÛŒØ±Ø§Ù†123",
    },
  ];

  // Ø¨Ø±Ø±Ø³ÛŒ Ø²Ù…Ø§Ù† ØªØ®Ù…ÛŒÙ†ÛŒ Ø´Ú©Ø³ØªÙ† Ø±Ù…Ø²Ù‡Ø§ÛŒ Ø¹Ø¨ÙˆØ±
  useEffect(() => {
    const fetchTimeEstimates = async () => {
      try {
        const response = await axios.post(
          `${API_BASE_URL}/api/hash/time-estimates`,
          {
            algorithm,
          }
        );

        if (response.data && response.data.data) {
          setTimeEstimates(response.data.data);
        }
      } catch (err) {
        console.error("Ø®Ø·Ø§ Ø¯Ø± Ø¯Ø±ÛŒØ§ÙØª Ø²Ù…Ø§Ù† ØªØ®Ù…ÛŒÙ†ÛŒ:", err);
      }
    };

    fetchTimeEstimates();
  }, [algorithm]);

  // ØªÙˆÙ„ÛŒØ¯ Ø±Ù…Ø²Ù‡Ø§ÛŒ Ø¹Ø¨ÙˆØ± Ù†Ù…ÙˆÙ†Ù‡
  const generateSamplePassword = async (complexity) => {
    try {
      setLoadingSamples((prev) => ({ ...prev, [complexity]: true }));

      const response = await axios.post(
        `${API_BASE_URL}/api/hash/sample-password`,
        {
          complexity,
        }
      );

      if (response.data && response.data.data) {
        setSamplePasswords((prev) => ({
          ...prev,
          [complexity]: response.data.data,
        }));
      }
    } catch (err) {
      console.error("Ø®Ø·Ø§ Ø¯Ø± ØªÙˆÙ„ÛŒØ¯ Ø±Ù…Ø² Ø¹Ø¨ÙˆØ± Ù†Ù…ÙˆÙ†Ù‡:", err);
    } finally {
      setLoadingSamples((prev) => ({ ...prev, [complexity]: false }));
    }
  };

  // ØªÙˆÙ„ÛŒØ¯ Ù‡Ù…Ù‡ Ø±Ù…Ø²Ù‡Ø§ÛŒ Ø¹Ø¨ÙˆØ± Ù†Ù…ÙˆÙ†Ù‡
  useEffect(() => {
    complexities.forEach((complexity) => {
      generateSamplePassword(complexity.value);
    });
  }, []);

  // Ø´Ú©Ø³ØªÙ† Ø±Ù…Ø² Ø¹Ø¨ÙˆØ±
  const crackPassword = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setCrackResult(null);
    setCrackingProgress(0);
    setStatusMessage("Ø¯Ø± Ø­Ø§Ù„ Ø´Ø±ÙˆØ¹ Ø¹Ù…Ù„ÛŒØ§Øª Ø´Ú©Ø³ØªÙ† Ø±Ù…Ø²...");

    // Ø´Ø¨ÛŒÙ‡â€ŒØ³Ø§Ø²ÛŒ Ù†Ù…Ø§ÛŒØ´ Ù¾ÛŒØ´Ø±ÙØª
    progressInterval.current = setInterval(() => {
      setCrackingProgress((prev) => {
        if (prev >= 95) return 95;
        const increment = Math.random() * 5 + 1;
        const newProgress = prev + increment;

        // Ù¾ÛŒØ§Ù…â€ŒÙ‡Ø§ÛŒ ÙˆØ¶Ø¹ÛŒØª Ø¨Ø± Ø§Ø³Ø§Ø³ Ù…ÛŒØ²Ø§Ù† Ù¾ÛŒØ´Ø±ÙØª
        if (newProgress > 20 && newProgress <= 40) {
          setStatusMessage("Ø¯Ø± Ø­Ø§Ù„ Ø¨Ø±Ø±Ø³ÛŒ Ø±Ù…Ø²Ù‡Ø§ÛŒ Ø¹Ø¨ÙˆØ± Ø±Ø§ÛŒØ¬...");
        } else if (newProgress > 40 && newProgress <= 60) {
          setStatusMessage("Ø¯Ø± Ø­Ø§Ù„ ØªØ³Øª ØªØ±Ú©ÛŒØ¨Ø§Øª Ù…Ø®ØªÙ„Ù...");
        } else if (newProgress > 60 && newProgress <= 80) {
          setStatusMessage("Ø¯Ø± Ø­Ø§Ù„ Ø¨Ø±Ø±Ø³ÛŒ Ø§Ù„Ú¯ÙˆÙ‡Ø§ÛŒ Ø±Ø§ÛŒØ¬...");
        } else if (newProgress > 80) {
          setStatusMessage("ØªÚ©Ù…ÛŒÙ„ Ø¹Ù…Ù„ÛŒØ§Øª...");
        }

        return newProgress;
      });
    }, 300);

    try {
      const response = await axios.post(`${API_BASE_URL}/api/hash/crack`, {
        hash: hashValue,
        algorithm,
        method,
      });

      if (response.data && response.data.data) {
        setCrackResult(response.data.data);
        setCrackingProgress(100);
        setStatusMessage("Ø¹Ù…Ù„ÛŒØ§Øª Ø¨Ø§ Ù…ÙˆÙÙ‚ÛŒØª Ø§Ù†Ø¬Ø§Ù… Ø´Ø¯!");
      } else {
        throw new Error("ÙØ±Ù…Øª Ù¾Ø§Ø³Ø® Ø³Ø±ÙˆØ± Ù†Ø§Ù…Ø¹ØªØ¨Ø± Ø§Ø³Øª");
      }
    } catch (err) {
      setError("Ø®Ø·Ø§ Ø¯Ø± Ø´Ø¨ÛŒÙ‡â€ŒØ³Ø§Ø²ÛŒ Ø´Ú©Ø³ØªÙ† Ø±Ù…Ø² Ø¹Ø¨ÙˆØ±. Ù„Ø·ÙØ§Ù‹ Ø¯ÙˆØ¨Ø§Ø±Ù‡ ØªÙ„Ø§Ø´ Ú©Ù†ÛŒØ¯.");
      console.error(err);
    } finally {
      setLoading(false);
      clearInterval(progressInterval.current);
    }
  };

  // ØªÙˆÙ„ÛŒØ¯ Ù‡Ø´ Ø¨Ø±Ø§ÛŒ Ø±Ù…Ø² Ø¹Ø¨ÙˆØ±
  const generateHashForCracking = async () => {
    if (!passwordToHash) return;

    try {
      const response = await axios.post(`${API_BASE_URL}/api/hash/generate`, {
        text: passwordToHash,
        algorithm,
      });

      if (response.data && response.data.data) {
        setGeneratedHash(response.data.data.hash);
        setHashValue(response.data.data.hash);
      }
    } catch (err) {
      console.error("Ø®Ø·Ø§ Ø¯Ø± ØªÙˆÙ„ÛŒØ¯ Ù‡Ø´:", err);
    }
  };

  // ÙØ±Ù…Øª Ø²Ù…Ø§Ù† Ø¨Ù‡ ØµÙˆØ±Øª Ø®ÙˆØ§Ù†Ø§
  const formatTime = (seconds) => {
    if (seconds < 0.001) {
      return "Ø¢Ù†ÛŒ";
    } else if (seconds < 1) {
      return `${(seconds * 1000).toFixed(2)} Ù…ÛŒÙ„ÛŒâ€ŒØ«Ø§Ù†ÛŒÙ‡`;
    } else if (seconds < 60) {
      return `${seconds.toFixed(2)} Ø«Ø§Ù†ÛŒÙ‡`;
    } else if (seconds < 3600) {
      return `${(seconds / 60).toFixed(2)} Ø¯Ù‚ÛŒÙ‚Ù‡`;
    } else if (seconds < 86400) {
      return `${(seconds / 3600).toFixed(2)} Ø³Ø§Ø¹Øª`;
    } else if (seconds < 31536000) {
      return `${(seconds / 86400).toFixed(2)} Ø±ÙˆØ²`;
    } else {
      return `${(seconds / 31536000).toFixed(2)} Ø³Ø§Ù„`;
    }
  };

  return (
    <div className="password-cracker">
      <h2>
        Ø´Ú©Ø³ØªÙ† Ø±Ù…Ø²Ø¹Ø¨ÙˆØ± (Password Cracker)
        <span className="subtitle">Ù¾ÛŒØ´Ø±ÙØªÙ‡</span>
      </h2>
      <p>Ø´Ø¨ÛŒÙ‡â€ŒØ³Ø§Ø²ÛŒ Ù¾ÛŒØ´Ø±ÙØªÙ‡ Ø¨Ø±Ø§ÛŒ Ù†Ù…Ø§ÛŒØ´ Ø¢Ø³ÛŒØ¨â€ŒÙ¾Ø°ÛŒØ±ÛŒ Ø±Ù…Ø²Ù‡Ø§ÛŒ Ø¹Ø¨ÙˆØ± Ø¶Ø¹ÛŒÙ</p>

      <div className="disclaimer">
        <strong>ØªÙˆØ¬Ù‡:</strong> Ø§ÛŒÙ† Ø§Ø¨Ø²Ø§Ø± ÙÙ‚Ø· Ø¨Ø±Ø§ÛŒ Ø§Ù‡Ø¯Ø§Ù Ø¢Ù…ÙˆØ²Ø´ÛŒ Ø·Ø±Ø§Ø­ÛŒ Ø´Ø¯Ù‡ Ùˆ
        Ù…ÙØ§Ù‡ÛŒÙ… Ù¾Ø§ÛŒÙ‡ Ø§Ù…Ù†ÛŒØª Ø±Ù…Ø² Ø¹Ø¨ÙˆØ± Ø±Ø§ Ù†Ù…Ø§ÛŒØ´ Ù…ÛŒâ€ŒØ¯Ù‡Ø¯.
      </div>

      <div className="sample-passwords-section">
        <h3>Ù†Ù…ÙˆÙ†Ù‡ Ø±Ù…Ø²Ù‡Ø§ÛŒ Ø¹Ø¨ÙˆØ± Ø¨Ø±Ø§ÛŒ Ø¢Ø²Ù…Ø§ÛŒØ´</h3>
        <p>
          Ø¨Ø±Ø§ÛŒ Ø¢Ø²Ù…Ø§ÛŒØ´ Ø³Ø±Ø¹Øª Ø´Ú©Ø³ØªÙ† Ø±Ù…Ø²Ù‡Ø§ÛŒ Ù…Ø®ØªÙ„ÙØŒ ÛŒÚ©ÛŒ Ø§Ø² Ù†Ù…ÙˆÙ†Ù‡â€ŒÙ‡Ø§ Ø±Ø§ Ø§Ù†ØªØ®Ø§Ø¨ Ú©Ù†ÛŒØ¯:
        </p>

        <div className="sample-passwords">
          {complexities.map((complexity) => (
            <div className="sample-password-card" key={complexity.value}>
