        <div className="form-row">
          <div className="form-group">
            <label htmlFor="algorithm">Ø§Ù„Ú¯ÙˆØ±ÛŒØªÙ… Ù‡Ø´:</label>
            <select
              id="algorithm"
              value={algorithm}
