            </select>
          </div>

          <div className="form-group">
            <label htmlFor="method">Ø±ÙˆØ´ Ø´Ú©Ø³ØªÙ†:</label>
            <select
              id="method"
              value={method}
              onChange={(e) => setMethod(e.target.value)}
            >
              {methods.map((m) => (
                <option key={m.value} value={m.value}>
                  {m.label}
                </option>
              ))}
            </select>
          </div>
        </div>

        <button type="submit" disabled={loading} className="btn">
          {loading ? "Ø¯Ø± Ø­Ø§Ù„ Ø´Ú©Ø³ØªÙ†..." : "Ø´Ø±ÙˆØ¹ Ø´Ú©Ø³ØªÙ† Ø±Ù…Ø² Ø¹Ø¨ÙˆØ±"}
        </button>
