          function generateSimpleHash(text) {
            let hash = 0;
            if (text.length === 0) return hash;
            for (let i = 0; i < text.length; i++) {
              const char = text.charCodeAt(i);
              hash = ((hash << 5) - hash) + char;
              hash = hash & hash;
            }
            return Math.abs(hash);
          }

          function updateSVGAvatar(elementId, hash) {
            const element = document.getElementById(elementId);
            if (!element) return;

            const h = hash.toString();
            const colorHue = parseInt(h.substring(0, 3)) % 360;

            let svg = '';

            if (elementId === 'github-embedded-avatar') {
              // شبیه‌سازی آواتار گیت‌هاب
              svg = createGitHubStyleAvatar(h, colorHue);
            } else if (elementId === 'gravatar-embedded-avatar') {
              // شبیه‌سازی آواتار گراواتار
              svg = createGravatarStyleAvatar(h, colorHue);
            } else if (elementId === 'dicebear-embedded-avatar') {
              // شبیه‌سازی آواتار دایس‌بیر
              svg = createDicebearStyleAvatar(h, colorHue);
            }

            element.innerHTML = svg;
          }

          function createGitHubStyleAvatar(hash, hue) {
            const grid = [];
            const size = 5;

            // ایجاد شبکه 5x5 با تقارن محوری
            for (let i = 0; i < size; i++) {
              const row = [];
              for (let j = 0; j < Math.ceil(size/2); j++) {
                const isOn = parseInt(hash[i*3 + j] || '0') % 2 === 1;
                row.push(isOn);
              }

              // اعمال تقارن
              for (let j = Math.floor(size/2) - 1; j >= 0; j--) {
                row.push(row[j]);
              }

              grid.push(row);
            }

            // ایجاد SVG
            let svg = \`<svg xmlns="http://www.w3.org/2000/svg" width="100%" height="100%" viewBox="0 0 ${size} ${size}">\`;
            svg += \`<rect width="${size}" height="${size}" fill="transparent"/>\`;

            const color = \`hsl(\${hue}, 70%, 50%)\`;

            for (let i = 0; i < size; i++) {
              for (let j = 0; j < size; j++) {
                if (grid[i][j]) {
                  svg += \`<rect x="\${j}" y="\${i}" width="1" height="1" fill="\${color}" />\`;
                }
              }
            }

            svg += '</svg>';
            return svg;
          }

          function createGravatarStyleAvatar(hash, hue) {
            const color = \`hsl(\${hue}, 70%, 50%)\`;
            const bgColor = \`hsl(\${hue}, 30%, 90%)\`;

            // ایجاد آواتار دایره‌ای با اشکال هندسی
            let svg = \`<svg xmlns="http://www.w3.org/2000/svg" width="100%" height="100%" viewBox="0 0 100 100">\`;
            svg += \`<circle cx="50" cy="50" r="50" fill="\${bgColor}"/>\`;

            // اضافه کردن اشکال مختلف بر اساس هش
            const shapeCount = parseInt(hash.substring(0, 2)) % 5 + 3;

            for (let i = 0; i < shapeCount; i++) {
              const x = 20 + (parseInt(hash.substring(i*2, i*2+2)) % 60);
              const y = 20 + (parseInt(hash.substring(i*2+2, i*2+4)) % 60);
              const size = 10 + (parseInt(hash.substring(i*2+4, i*2+6)) % 30);

              const shapeType = parseInt(hash.substring(i*3, i*3+1)) % 4;

              switch(shapeType) {
                case 0:
                  svg += \`<circle cx="\${x}" cy="\${y}" r="\${size/2}" fill="\${color}" opacity="0.7"/>\`;
                  break;
                case 1:
                  svg += \`<rect x="\${x-size/2}" y="\${y-size/2}" width="\${size}" height="\${size}" fill="\${color}" opacity="0.7"/>\`;
                  break;
                case 2:
                  svg += \`<polygon points="\${x},\${y-size/2} \${x+size/2},\${y+size/2} \${x-size/2},\${y+size/2}" fill="\${color}" opacity="0.7"/>\`;
                  break;
                case 3:
                  svg += \`<ellipse cx="\${x}" cy="\${y}" rx="\${size/2}" ry="\${size/3}" fill="\${color}" opacity="0.7"/>\`;
                  break;
              }
            }

            svg += '</svg>';
            return svg;
          }

          function createDicebearStyleAvatar(hash, hue) {
            const color = \`hsl(\${hue}, 70%, 50%)\`;
            const bgColor = \`hsl(\${hue}, 20%, 95%)\`;

            // ایجاد شبکه 4x4 برای آواتار پیکسلی
            const grid = [];
            const size = 4;

            // ایجاد شبکه با تقارن محوری
            for (let i = 0; i < size; i++) {
              const row = [];
              for (let j = 0; j < Math.ceil(size/2); j++) {
                const isOn = parseInt(hash[i*3 + j] || '0') % 2 === 1;
                row.push(isOn);
              }

              // اعمال تقارن
              for (let j = Math.ceil(size/2) - 1 - (size % 2 === 0 ? 1 : 0); j >= 0; j--) {
