              }

              grid.push(row);
            }

            // ایجاد SVG
            let svg = \`<svg xmlns="http://www.w3.org/2000/svg" width="100%" height="100%" viewBox="0 0 ${size} ${size}">\`;
            svg += \`<rect width="${size}" height="${size}" fill="${bgColor}"/>\`;

            for (let i = 0; i < size; i++) {
              for (let j = 0; j < size; j++) {
                if (grid[i][j]) {
                  svg += \`<rect x="\${j}" y="\${i}" width="1" height="1" fill="\${color}" />\`;
                }
              }
            }

            svg += '</svg>';
            return svg;
          }

          // اجرای اولیه برای نمایش آواتارها
          window.addEventListener('load', function() {
            const defaultHash = generateSimpleHash('example');
            updateSVGAvatar('github-embedded-avatar', defaultHash);
            updateSVGAvatar('gravatar-embedded-avatar', defaultHash);
            updateSVGAvatar('dicebear-embedded-avatar', defaultHash);
          });
        `
