import React, { useState } from "react";
import axios from "axios";
import "../styles/HashGenerator.css";
import { API_BASE_URL } from "../config";

const HashGenerator = () => {
  const [inputText, setInputText] = useState("");
  const [algorithm, setAlgorithm] = useState("md5");
  const [hashResult, setHashResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const algorithms = ["md5", "sha1", "sha256", "sha512"];

  const generateHash = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      // Add timeout to prevent hanging requests
      const response = await axios.post(
        `${API_BASE_URL}/api/hash/generate`,
        {
          text: inputText,
          algorithm,
        },
        { timeout: 10000 }
      );

      // Check if response has the expected structure
      if (response.data && response.data.data) {
        setHashResult(response.data.data);
      } else {
        throw new Error("Invalid response format from server");
      }
    } catch (err) {
      console.error("Hash generation error:", err);
      setError(
        err.response?.data?.message ||
          err.message ||
          "Error connecting to server. Please check if the backend is running."
      );
    } finally {
      setLoading(false);
    }
  };

  const copyToClipboard = () => {
    if (hashResult?.hash) {
      navigator.clipboard.writeText(hashResult.hash);
      alert("Hash copied to clipboard!");
    }
  };

  return (
    <div className="hash-generator">
      <h2>Hash Generator</h2>
      <p>Generate hash values using different algorithms</p>

      <form onSubmit={generateHash}>
        <div className="form-group">
          <label htmlFor="inputText">Enter text to hash:</label>
          <textarea
            id="inputText"
            value={inputText}
            onChange={(e) => setInputText(e.target.value)}
            required
            placeholder="Enter text..."
          ></textarea>
        </div>

        <div className="form-group">
          <label htmlFor="algorithm">Select hash algorithm:</label>
          <select
            id="algorithm"
            value={algorithm}
            onChange={(e) => setAlgorithm(e.target.value)}
          >
            {algorithms.map((algo) => (
              <option key={algo} value={algo}>
                {algo.toUpperCase()}
              </option>
            ))}
          </select>
        </div>

        <button type="submit" disabled={loading} className="btn">
          {loading ? "Generating..." : "Generate Hash"}
        </button>
      </form>

      {error && (
        <div className="error">
          <p>{error}</p>
          <p>
            Tips: Make sure the backend server is running on port 5000 or update
            the port in config.js
          </p>
        </div>
      )}

      {hashResult && (
        <div className="result">
          <h3>Hash Result:</h3>
          <div className="hash-box">
            <p>{hashResult.hash}</p>
            <button onClick={copyToClipboard} className="copy-btn">
              Copy
            </button>
          </div>
          <div className="result-details">
            <p>
              <strong>Algorithm:</strong> {hashResult.algorithm.toUpperCase()}
            </p>
            <p>
              <strong>Length:</strong> {hashResult.hash.length} characters
            </p>
          </div>
        </div>
      )}

      <div className="info-box">
        <h3>About {algorithm.toUpperCase()}</h3>
        {algorithm === "md5" && (
          <p>
            MD5 produces a 128-bit (16-byte) hash value. It was designed by
            Ronald Rivest in 1991 and is no longer considered secure for
            cryptographic purposes due to collision vulnerabilities.
          </p>
        )}
        {algorithm === "sha1" && (
          <p>
            SHA-1 produces a 160-bit (20-byte) hash value. Developed by the NSA,
            it's no longer recommended for security applications due to
            theoretical attacks.
          </p>
        )}
        {algorithm === "sha256" && (
          <p>
            SHA-256 is part of the SHA-2 family, producing a 256-bit (32-byte)
            hash. It's widely used in security applications and cryptocurrencies
            like Bitcoin.
          </p>
        )}
        {algorithm === "sha512" && (
          <p>
            SHA-512 produces a 512-bit (64-byte) hash value, offering higher
            security than SHA-256. It's used in applications requiring enhanced
            security.
          </p>
        )}
      </div>
    </div>
  );
};

export default HashGenerator;
