import React, { useState, useEffect } from 'react';
import '../styles/PasswordManager.css';

const PasswordManager = () => {
  const [masterPassword, setMasterPassword] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [passwords, setPasswords] = useState([]);
  const [showAddForm, setShowAddForm] = useState(false);
  const [newPassword, setNewPassword] = useState({
    title: '',
    username: '',
    password: '',
    url: '',
    notes: ''
  });
  const [searchTerm, setSearchTerm] = useState('');
  const [editingIndex, setEditingIndex] = useState(null);
  const [showPassword, setShowPassword] = useState({});
  
  useEffect(() => {
    // Ø¯Ø± Ø¯Ù†ÛŒØ§ÛŒ ÙˆØ§Ù‚Ø¹ÛŒØŒ Ø§ÛŒÙ† Ø¯Ø§Ø¯Ù‡â€ŒÙ‡Ø§ Ø¨Ø§ÛŒØ¯ Ø§Ø² Ø³Ø±ÙˆØ± Ø¨Ø§ Ø±Ù…Ø²Ù†Ú¯Ø§Ø±ÛŒ Ù…Ù†Ø§Ø³Ø¨ Ø¯Ø±ÛŒØ§ÙØª Ø´ÙˆÙ†Ø¯
    const savedPasswords = localStorage.getItem('encryptedPasswords');
    if (savedPasswords && isLoggedIn) {
      try {
        // Ø´Ø¨ÛŒÙ‡â€ŒØ³Ø§Ø²ÛŒ Ø±Ù…Ø²Ú¯Ø´Ø§ÛŒÛŒ
        const decryptedPasswords = JSON.parse(savedPasswords);
        setPasswords(decryptedPasswords);
      } catch (error) {
        console.error('Ø®Ø·Ø§ Ø¯Ø± Ø®ÙˆØ§Ù†Ø¯Ù† Ø±Ù…Ø²Ù‡Ø§ÛŒ Ø¹Ø¨ÙˆØ±:', error);
      }
