import React, { useState, useEffect } from 'react';
import '../styles/PasswordManager.css';

const PasswordManager = () => {
  const [masterPassword, setMasterPassword] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [passwords, setPasswords] = useState([]);
  const [showAddForm, setShowAddForm] = useState(false);
  const [newPassword, setNewPassword] = useState({
    title: '',
    username: '',
    password: '',
    url: '',
    notes: ''
  });
  const [searchTerm, setSearchTerm] = useState('');
  const [editingIndex, setEditingIndex] = useState(null);
  const [showPassword, setShowPassword] = useState({});
  
  useEffect(() => {
    // در دنیای واقعی، این داده‌ها باید از سرور با رمزنگاری مناسب دریافت شوند
    const savedPasswords = localStorage.getItem('encryptedPasswords');
    if (savedPasswords && isLoggedIn) {
      try {
        // شبیه‌سازی رمزگشایی
        const decryptedPasswords = JSON.parse(savedPasswords);
        setPasswords(decryptedPasswords);
      } catch (error) {
        console.error('خطا در خواندن رمزهای عبور:', error);
      }
    }
  }, [isLoggedIn]);
  
  // ذخیره رمزهای عبور در حافظه محلی (این فقط برای نمایش است و امن نیست)
  useEffect(() => {
    if (isLoggedIn && passwords.length > 0) {
      localStorage.setItem('encryptedPasswords', JSON.stringify(passwords));
    }
  }, [passwords, isLoggedIn]);
  
  // احراز هویت (این یک نمونه آموزشی است و امن نیست)
  const handleLogin = (e) => {
    e.preventDefault();
    // در نسخه واقعی، باید از روش‌های رمزنگاری پیشرفته و هش استفاده کرد
    if (masterPassword.length >= 4) {
      setIsLoggedIn(true);
      
      // برای نمایش، رمزهای عبور نمونه اضافه می‌کنیم
      if (!localStorage.getItem('encryptedPasswords')) {
        setPasswords([
          {
            id: 1,
            title: 'ایمیل شخصی',
            username: 'yasin.example@gmail.com',
            password: 'تغییر داده نشده',
            url: 'https://mail.google.com',
            notes: 'ایمیل اصلی برای کارهای شخصی',
            createdAt: new Date().toLocaleDateString('fa-IR')
          },
          {
            id: 2,
            title: 'حساب بانکی',
            username: 'yasin1234',
            password: 'تغییر داده نشده',
            url: 'https://bank.example.com',
            notes: 'این فقط یک نمایش است - هرگز رمز واقعی بانک را اینجا ذخیره نکنید',
            createdAt: new Date().toLocaleDateString('fa-IR')
          }
        ]);
      }
    }
  };
  
  // خروج از سیستم
  const handleLogout = () => {
    setIsLoggedIn(false);
    setMasterPassword('');
  };
  
  // اضافه کردن یا ویرایش رمز عبور جدید
  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (editingIndex !== null) {
      // ویرایش رمز عبور موجود
      const updatedPasswords = [...passwords];
      updatedPasswords[editingIndex] = {
        ...newPassword,
        updatedAt: new Date().toLocaleDateString('fa-IR')
      };
      setPasswords(updatedPasswords);
      setEditingIndex(null);
    } else {
      // اضافه کردن رمز عبور جدید
      setPasswords([
        ...passwords,
        {
          ...newPassword,
          id: Date.now(),
          createdAt: new Date().toLocaleDateString('fa-IR')
        }
      ]);
    }
    
    // پاک کردن فرم
    setNewPassword({ title: '', username: '', password: '', url: '', notes: '' });
    setShowAddForm(false);
  };
  
  // شروع ویرایش رمز عبور
  const startEditing = (index) => {
    setNewPassword(passwords[index]);
    setEditingIndex(index);
    setShowAddForm(true);
  };
  
  // حذف رمز عبور
  const deletePassword = (index) => {
    if (window.confirm('آیا مطمئن هستید که می‌خواهید این رمز عبور را حذف کنید؟')) {
      const updatedPasswords = passwords.filter((_, i) => i !== index);
      setPasswords(updatedPasswords);
    }
  };
  
  // نمایش یا مخفی کردن رمز عبور
  const togglePasswordVisibility = (index) => {
    setShowPassword(prev => ({
      ...prev,
      [index]: !prev[index]
    }));
  };
  
  // تولید رمز عبور تصادفی
  const generateRandomPassword = () => {
    const chars = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789!@#$%^&*()';
    let result = '';
    const length = 12;
    for (let i = 0; i < length; i++) {
      result += chars.charAt(Math.floor(Math.random() * chars.length));
    }
    setNewPassword({ ...newPassword, password: result });
  };
  
  // کپی کردن در کلیپ‌بورد
  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
    alert('با موفقیت در کلیپ‌بورد کپی شد!');
  };
  
  // فیلتر رمزهای عبور بر اساس جستجو
  const filteredPasswords = passwords.filter(item => 
    item.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    item.username.toLowerCase().includes(searchTerm.toLowerCase()) ||
    item.url.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="password-manager">
      <h2>مدیریت رمزهای عبور</h2>
      <p>ذخیره و مدیریت ایمن رمزهای عبور با استفاده از رمزنگاری پیشرفته</p>
      
      {!isLoggedIn ? (
        <div className="login-container">
          <div className="login-form">
            <h3>ورود به مدیریت رمزعبور</h3>
            <p>لطفاً رمز اصلی خود را وارد کنید</p>
            
            <form onSubmit={handleLogin}>
              <div className="form-group">
                <label htmlFor="masterPassword">رمز اصلی:</label>
                <input
                  type="password"
                  id="masterPassword"
                  value={masterPassword}
                  onChange={(e) => setMasterPassword(e.target.value)}
                  required
                  minLength={4}
                  placeholder="رمز اصلی خود را وارد کنید..."
                />
              </div>
              
              <button type="submit" className="btn">ورود</button>
              
              <div className="info-text">
                <p><strong>توجه:</strong> این یک نمونه نمایشی است. برای تست می‌توانید هر رمز عبوری با حداقل ۴ کاراکتر وارد کنید.</p>
              </div>
            </form>
          </div>
          
          <div className="login-info">
            <h3>درباره مدیریت رمزعبور</h3>
            <p>مدیریت رمزعبور روشی امن برای ذخیره تمام رمزهای عبور شماست. با استفاده از یک رمز اصلی، می‌توانید به تمام رمزهای عبور خود دسترسی داشته باشید.</p>
            
            <h4>مزایای استفاده:</h4>
            <ul>
              <li>ذخیره رمزهای عبور به صورت رمزنگاری شده</li>
              <li>دسترسی آسان با یک رمز اصلی</li>
              <li>ایجاد رمزهای عبور قوی و تصادفی</li>
              <li>محافظت از سرقت اطلاعات</li>
            </ul>
          </div>
        </div>
      ) : (
        <div className="dashboard">
          <div className="dashboard-header">
            <div className="search-container">
              <input
                type="text"
                placeholder="جستجو..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="search-input"
              />
            </div>
            
            <div className="action-buttons">
              <button
                className="btn btn-add"
                onClick={() => {
                  setShowAddForm(true);
                  setEditingIndex(null);
                  setNewPassword({ title: '', username: '', password: '', url: '', notes: '' });
                }}
              >
                افزودن رمز عبور جدید
              </button>
              <button className="btn btn-logout" onClick={handleLogout}>
                خروج
              </button>
            </div>
          </div>
          
          {showAddForm && (
            <div className="password-form-container">
              <div className="password-form">
                <h3>{editingIndex !== null ? 'ویرایش رمز عبور' : 'افزودن رمز عبور جدید'}</h3>
                <form onSubmit={handleSubmit}>
                  <div className="form-group">
                    <label htmlFor="title">عنوان:</label>
                    <input
                      type="text"
                      id="title"
                      value={newPassword.title}
                      onChange={(e) => setNewPassword({...newPassword, title: e.target.value})}
                      required
                      placeholder="مثال: ایمیل کاری"
                    />
                  </div>
                  
                  <div className="form-group">
                    <label htmlFor="username">نام کاربری:</label>
                    <input
                      type="text"
                      id="username"
                      value={newPassword.username}
                      onChange={(e) => setNewPassword({...newPassword, username: e.target.value})}
                      required
                      placeholder="نام کاربری یا ایمیل"
                    />
                  </div>
                  
                  <div className="form-group password-group">
                    <label htmlFor="password">رمز عبور:</label>
                    <div className="password-input-container">
                      <input
                        type="text"
                        id="password"
                        value={newPassword.password}
                        onChange={(e) => setNewPassword({...newPassword, password: e.target.value})}
                        required
                        placeholder="رمز عبور"
                      />
                      <button 
                        type="button" 
                        className="generate-btn"
                        onClick={generateRandomPassword}
                      >
                        تولید
                      </button>
                    </div>
                  </div>
                  
                  <div className="form-group">
                    <label htmlFor="url">آدرس سایت:</label>
                    <input
                      type="url"
                      id="url"
                      value={newPassword.url}
                      onChange={(e) => setNewPassword({...newPassword, url: e.target.value})}
                      placeholder="https://example.com"
                    />
                  </div>
                  
                  <div className="form-group">
                    <label htmlFor="notes">یادداشت:</label>
                    <textarea
                      id="notes"
                      value={newPassword.notes}
                      onChange={(e) => setNewPassword({...newPassword, notes: e.target.value})}
                      placeholder="یادداشت اضافی..."
                      rows="3"
                    ></textarea>
                  </div>
                  
                  <div className="form-actions">
                    <button type="submit" className="btn btn-save">
                      {editingIndex !== null ? 'بروزرسانی' : 'ذخیره'}
                    </button>
                    <button 
                      type="button" 
                      className="btn btn-cancel"
                      onClick={() => setShowAddForm(false)}
                    >
                      انصراف
                    </button>
                  </div>
                </form>
              </div>
            </div>
          )}
          
          <div className="passwords-container">
            {filteredPasswords.length > 0 ? (
              filteredPasswords.map((item, index) => (
                <div className="password-card" key={item.id}>
                  <div className="password-header">
                    <h3>{item.title}</h3>
                    <div className="password-actions">
                      <button
                        className="action-btn edit-btn"
                        onClick={() => startEditing(index)}
                        title="ویرایش"
                      >
                        ✏️
                      </button>
                      <button
                        className="action-btn delete-btn"
                        onClick={() => deletePassword(index)}
                        title="حذف"
                      >
                        🗑️
                      </button>
                    </div>
                  </div>
                  
                  <div className="password-details">
                    <div className="detail-row">
                      <span className="detail-label">نام کاربری:</span>
                      <span className="detail-value">
                        {item.username}
                        <button
                          className="copy-btn"
                          onClick={() => copyToClipboard(item.username)}
                          title="کپی"
                        >
                          📋
                        </button>
                      </span>
                    </div>
                    
                    <div className="detail-row">
                      <span className="detail-label">رمز عبور:</span>
                      <span className="detail-value password-value">
                        {showPassword[index] ? item.password : '•••••••••'}
                        <button
                          className="visibility-btn"
                          onClick={() => togglePasswordVisibility(index)}
                          title={showPassword[index] ? "مخفی کردن" : "نمایش"}
                        >
                          {showPassword[index] ? '👁️' : '👁️‍🗨️'}
                        </button>
                        <button
                          className="copy-btn"
                          onClick={() => copyToClipboard(item.password)}
                          title="کپی"
                        >
                          📋
                        </button>
                      </span>
                    </div>
                    
                    {item.url && (
                      <div className="detail-row">
                        <span className="detail-label">سایت:</span>
                        <span className="detail-value url-value">
                          <a href={item.url} target="_blank" rel="noopener noreferrer">
                            {item.url}
                          </a>
                        </span>
                      </div>
                    )}
                    
                    {item.notes && (
                      <div className="detail-row notes-row">
                        <span className="detail-label">یادداشت:</span>
                        <span className="detail-value notes-value">{item.notes}</span>
                      </div>
                    )}
                    
                    <div className="detail-row date-row">
                      <span className="detail-label">تاریخ ایجاد:</span>
                      <span className="detail-value date-value">{item.createdAt}</span>
                    </div>
                  </div>
                </div>
              ))
            ) : (
              <div className="no-passwords">
                {searchTerm ? (
                  <p>هیچ نتیجه‌ای برای جستجوی شما پیدا نشد.</p>
                ) : (
                  <p>هنوز رمز عبوری ذخیره نشده است. با کلیک روی دکمه "افزودن رمز عبور جدید" شروع کنید.</p>
                )}
              </div>
            )}
          </div>
        </div>
      )}
      
      <div className="info-box">
        <h3>امنیت در مدیریت رمز عبور</h3>
        <p>مدیریت رمز عبور از تکنیک‌های رمزنگاری پیشرفته برای محافظت از داده‌های حساس استفاده می‌کند:</p>
        <ul>
          <li><strong>رمزنگاری AES-256:</strong> برای رمزگذاری رمزهای عبور ذخیره شده</li>
          <li><strong>رمزنگاری کلید مشتق شده:</strong> رمز اصلی شما برای تولید کلید رمزنگاری استفاده می‌شود</li>
          <li><strong>ذخیره‌سازی ایمن:</strong> داده‌ها به صورت رمزگذاری شده ذخیره می‌شوند و هرگز به صورت متن ساده در پایگاه داده قرار نمی‌گیرند</li>
          <li><strong>تأیید هویت چند عاملی:</strong> برای افزایش امنیت، می‌توانید تأیید هویت دو مرحله‌ای را فعال کنید</li>
        </ul>
        <p><strong>توجه:</strong> این نسخه نمایشی است و برای اهداف آموزشی طراحی شده است. برای استفاده واقعی، از نرم‌افزارهای مدیریت رمز عبور معتبر استفاده کنید.</p>
      </div>
    </div>
  );
};

export default PasswordManager;
