import React, { useState } from 'react';
import '../styles/FileDeduplicator.css';

const FileDeduplicator = () => {
  const [files, setFiles] = useState([]);
  const [duplicates, setDuplicates] = useState([]);
  const [scanning, setScanning] = useState(false);
  const [progress, setProgress] = useState(0);
  const [showResults, setShowResults] = useState(false);

  // محاسبه هش برای فایل‌ها - در واقعیت این کار باید در سمت سرور انجام شود
  const calculateHash = async (file) => {
    return new Promise((resolve) => {
      const reader = new FileReader();
      
      reader.onload = (event) => {
        const buffer = event.target.result;
        // این یک شبیه‌سازی است - در واقعیت باید از SHA-256 یا الگوریتم مناسب استفاده شود
        const hash = Array.from(new Uint8Array(buffer))
          .slice(0, 1000) // برای سرعت، فقط بخشی از فایل را هش می‌کنیم
          .reduce((a, b) => a + b, 0)
          .toString(16);
          
        resolve(hash);
      };
      
      // خواندن بخشی از فایل برای شبیه‌سازی
      const slice = file.slice(0, 1000000); // 1MB اول فایل
      reader.readAsArrayBuffer(slice);
    });
  };

  // پردازش فایل‌ها و پیدا کردن موارد تکراری
  const processFiles = async (selectedFiles) => {
    setScanning(true);
    setProgress(0);
    setFiles(Array.from(selectedFiles));
    
    const fileWithHash = [];
    const hashMap = {};
    let duplicateGroups = [];
    
    // محاسبه هش برای هر فایل
    for (let i = 0; i < selectedFiles.length; i++) {
      const file = selectedFiles[i];
      const hash = await calculateHash(file);
      
      fileWithHash.push({
        file,
        hash,
        name: file.name,
        size: file.size,
        type: file.type,
        lastModified: new Date(file.lastModified).toLocaleDateString('fa-IR'),
      });
      
      // بررسی فایل‌های تکراری
      if (hashMap[hash]) {
        hashMap[hash].push(fileWithHash[i]);
      } else {
        hashMap[hash] = [fileWithHash[i]];
      }
      
      setProgress(Math.floor((i + 1) / selectedFiles.length * 100));
    }
    
    // پیدا کردن گروه‌های فایل تکراری
    for (const hash in hashMap) {
      if (hashMap[hash].length > 1) {
        duplicateGroups.push(hashMap[hash]);
      }
    }
    
    setDuplicates(duplicateGroups);
    setScanning(false);
    setShowResults(true);
  };

  const handleFileChange = (event) => {
    const selectedFiles = event.target.files;
    if (selectedFiles.length > 0) {
      processFiles(selectedFiles);
    }
  };
  
  // تبدیل اندازه فایل به فرمت خوانا
  const formatFileSize = (bytes) => {
    if (bytes < 1024) return bytes + ' بایت';
    else if (bytes < 1048576) return (bytes / 1024).toFixed(1) + ' کیلوبایت';
    else if (bytes < 1073741824) return (bytes / 1048576).toFixed(1) + ' مگابایت';
    else return (bytes / 1073741824).toFixed(1) + ' گیگابایت';
  };

  return (
    <div className="file-deduplicator">
      <h2>پیدا کردن فایل‌های تکراری</h2>
      <p>از این ابزار برای شناسایی فایل‌های تکراری در سیستم خود استفاده کنید. این ابزار از توابع هش برای مقایسه محتوای فایل‌ها استفاده می‌کند.</p>
      
      <div className="upload-section">
        <div className="file-input-container">
          <input
            type="file"
            id="file-input"
            multiple
            onChange={handleFileChange}
            disabled={scanning}
          />
          <label htmlFor="file-input" className={scanning ? 'disabled' : ''}>
            {scanning ? 'در حال اسکن...' : 'انتخاب فایل‌ها'}
          </label>
          <p className="file-count">
            {files.length > 0 ? `${files.length} فایل انتخاب شده` : 'فایلی انتخاب نشده است'}
          </p>
        </div>
      </div>
      
      {scanning && (
        <div className="scanning-progress">
          <div className="progress-bar">
            <div className="progress-fill" style={{ width: `${progress}%` }}></div>
          </div>
          <p>در حال تحلیل فایل‌ها... {progress}%</p>
        </div>
      )}
      
      {showResults && !scanning && (
        <div className="results-section">
          <div className="results-header">
            <h3>نتایج تحلیل</h3>
            <p>
              {duplicates.length > 0 
                ? `${duplicates.length} گروه فایل تکراری پیدا شد.`
                : 'هیچ فایل تکراری پیدا نشد.'}
            </p>
          </div>
          
          {duplicates.length > 0 && (
            <div className="duplicate-groups">
              {duplicates.map((group, groupIndex) => (
                <div className="duplicate-group" key={groupIndex}>
                  <h4>گروه فایل‌های تکراری {groupIndex + 1}</h4>
                  <p className="hash-info">هش مشترک: <code>{group[0].hash}</code></p>
                  
                  <table className="file-table">
                    <thead>
                      <tr>
                        <th>نام فایل</th>
                        <th>اندازه</th>
                        <th>نوع</th>
                        <th>آخرین تغییر</th>
                      </tr>
                    </thead>
                    <tbody>
                      {group.map((file, fileIndex) => (
                        <tr key={fileIndex}>
                          <td>{file.name}</td>
                          <td>{formatFileSize(file.size)}</td>
                          <td>{file.type || 'نامشخص'}</td>
                          <td>{file.lastModified}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              ))}
            </div>
          )}
        </div>
      )}
      
      <div className="info-box">
        <h3>درباره پیدا کردن فایل‌های تکراری</h3>
        <p>این ابزار از توابع هش برای مقایسه محتوای فایل‌ها استفاده می‌کند. در روش کارکرد این ابزار:</p>
        <ol>
          <li>هر فایل خوانده شده و هش محتوای آن محاسبه می‌شود</li>
          <li>فایل‌هایی که هش یکسانی دارند به عنوان تکراری شناسایی می‌شوند</li>
          <li>نتایج به صورت گروه‌های فایل‌های تکراری نمایش داده می‌شوند</li>
        </ol>
        <p>
          <strong>توجه:</strong> این نسخه نمایشی از برنامه، تنها بخش کوچکی از فایل را برای محاسبه هش استفاده می‌کند.
          در نسخه واقعی، تمام محتوای فایل باید هش شود تا دقت کامل داشته باشد.
        </p>
      </div>
    </div>
  );
};

export default FileDeduplicator;
